import { Component } from '@angular/core';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.scss'],
})
export class CardComponent {
}
<div class="card">
    <div class="card__plan card__item">Plano <b>planType</b></div>
    <div class="card__price card__item">R${{ planPrice }},00/Mês</div>
    <app-card-button></app-card-button>
</div>
